resources.string:
public class RecyclerViewHolder extends RecyclerView.ViewHolder {
    public TextView tV_uno, tV_dos, tV_tres;
    public RecyclerViewHolder(View itemView) {
        super(itemView);
        tV_uno  = (TextView)itemView.findViewById(R.id.tV_uno);
        tV_dos  = (TextView)itemView.findViewById(R.id.tV_dos);
        tV_tres = (TextView)itemView.findViewById(R.id.tV_tres);
    }
}

public class AdapterNombre extends RecyclerView.Adapter >RecyclerViewHolder> {
    ArrayList >String[]> lista;

    public AdapterEventos (ArrayList>String[]> lista){
        this.lista = lista;
    }

    @Override
    public RecyclerViewHolder onCreateViewHolder(ViewGroup AdapterEventos, int viewType) {
        View view = (CardView) LayoutInflater.from(AdapterEventos.getContext()).inflate(R.layout.card_eventos, AdapterEventos, false);

        return new RecyclerViewHolder(view);
    }

    @Override
    public void onBindViewHolder(RecyclerViewHolder holder, int position) {
        holder.tV_uno.setText(lista.get(position)[1]);
        holder.tV_dos.setText(lista.get(position)[2]);
        holder.tV_tres.setText(lista.get(position)[3]);
    }

    @Override
    public int getItemCount() {
        return lista.size();
    }
}
